INSERT INTO `author` (`author_id`, `author_name`, `author_bio`, `author_image_path`, `author_active`) VALUES
(14, 'Неизвестный художник (НХ) 1800-е гг.', 'Неизвестный художник IXX века.', '@uploads/author/_portrait_empty.jpg', 1),
(15, 'Неизвестный художник (НХ) 1900–1950', 'Неизвестный художник первой половины XX века.', '@uploads/author/_portrait_empty.jpg', 1),
(16, 'Неизвестный художник (НХ) 1950–2000', 'Неизвестный художник второй половины XX века.', '@uploads/author/_portrait_empty.jpg', 1),
(17, 'Антокольский Марк Матвеевич', '1843, Вильно – 1902, Бад-Гомбург, Германия\r\n\r\nРодился в семье трактирщика. С детства увлекался резьбой по дереву, кости и металлу. В 1862–1868 как вольнослушатель посещал занятия на скульптурном отделении Академии художеств сначала у Н.С.Пименова, затем у И.И.Реймерса. В 1864 награжден малой серебряной медалью за горельеф из дерева "Еврей-портной", а в 1865 – большой серебряной медалью за горельеф "Еврей-скупой", выполненный из слоновой кости и дерева.\r\n', '@uploads/author/antokolsky_mark.jpg', 1),
(18, 'Белютин Элий Михайлович', '1925, Москва - 2012, Москва\r\n\r\nВ 1941 ушел добровольцем на фронт, был контужен и демобилизован. В 1942–1947 учился в Московском художественном институте им. В.И.Сурикова. Элий Белютин был учеником П.Кузнецова, А.Лентулова, Л.Бруни, для которых В.Кандинский, К.Малевич, М.Шагал, Э.Лисицкий были не только именами из энциклопедий, а живыми людьми, с которыми они дружили и вместе выставлялись.\r\n', '@uploads/author/belutin_eliy.jpg', 1),
(19, 'Бенуа Александр Николаевич', '1870 С.-Петербург – 1960, Париж\r\n\r\nГрафик, художник театра и кино. Иллюстратор, оформитель книг, художник-декоратор, живописец. Историк искусства и художественный критик. Музейный деятель и режиссер. \r\n', '@uploads/author/benua_aleksandr.jpg', 1),
(20, 'Борисов-Мусатов Виктор', '1870, Саратов – 1905, Таруса\r\n\r\nУчился в Московском училище живописи, ваяния и зодчества в 1890–1891 и 1893–1895, а также в Императорской Академии художеств в Санкт-Петербурге в 1891–1893 в качестве вольнослушателя. Обучался в Париже в студии Ф.Кормона в 1895–1898. Испытал сильное впечатление от творчества художника-символиста П.Пюви де Шаванна. \r\n', '@uploads/author/borisov-musatov_victor.jpg', 1),
(21, 'Вейсберг Владимир Григорьевич', '1924, Москва – 1985, Москва\r\n\r\nВ 1943–1948 учился в художественной студии ВЦСПС у С.Н.Ивашева-Мусатова и посещал студию И.И.Машкова и А.А.Осмеркина. Член "Группы восьми". В 1959-1984 годах преподавал живопись в студии Института повышения квалификации Союза архитекторов СССР, которую называли "школа Вейсберга".\r\n', '@uploads/author/vaisberg_vladimir.jpg', 1),
(22, 'Верещагин Василий Васильевич', '1842, Череповец – 1904, погиб при взрыве броненосца «Петропавловск» вблизи Порт-Артура во время русско-японской войны\r\n\r\nРодился в семье помещика, предводителя дворянства. Воспитывался в Морском кадетском корпусе в Санкт-Петербурге в 1853–1860. С 1858 посещал занятия в Рисовальной школе Общества поощрения художников.\r\n', '@uploads/author/vereshagin_vasily.jpg', 1),
(23, 'Волков Александр Николаевич', '1886, Скобелев, Фергана – 1957, Ташкент\r\n\r\nУчился в мастерской акварелиста Д.И.Бортникера (1907), затем – в Высшем художественном училище Императорской Академии художеств в Санкт-Петербурге (ИАХ) в 1908–1910 у В.Е.Маковского.\r\n', '@uploads/author/volkov_aleksandr.jpg', 1),
(24, 'Гадаев Лазарь Тазеевич', '1938, село Сурх-Дигора Северо-Осетинской АССР - 2008, Москва\r\n\r\nУчился в Северо-Осетинском педагогическом училище в г. Орджоникидзе на художественно-графическом факультете в 1956–1960; затем – в Московском государственном художественном институте им. В.И.Сурикова в 1960–1966 у М.Г.Манизера и Д.Д.Жилинского. Работает преимущественно в камне, бронзе, керамике.\r\n', '@uploads/author/gadaev_lazar.jpg', 1),
(25, 'Герасимов Александр Михайлович', '1881, Козлов Тамбовской губернии – 1963, Москва\r\n\r\nУчился в Московском училище живописи, ваяния и зодчества (МУЖВЗ) на живописном отделении в 1903–1916 у А.Е.Архипова, К.А.Коровина, В.А.Серова; с 1910 – на архитектурном отделении у В.О.Ключевского.\r\n', '@uploads/author/gerasimov_aleksandr.jpg', 1),
(26, 'Головин Александр Яковлевич', '1863, Москва – 1930, Детское Село, ныне Пушкин Ленинградской области\r\n\r\nУчился в Московском училище живописи, ваяния и зодчества в 1881–1889 у И.М.Прянишникова, В.Е.Маковского, В.Д.Поленова, Е.С.Сорокина, затем – в Париже в частной школе Ф.Коларосси в 1889 и академии Витти в 1897. Действительный член Императорской Академии художеств в Санкт-Петербурге (с 1912).\r\n', '@uploads/author/golovin_aleksandr.jpg', 1),
(27, 'Древин Александр Давидович', '1889, Венден, Лифляндская губерния – 1938\r\n\r\nУчился в Рижской художественной школе в 1906–1913 у В.Е.Пурвита. Преподавал в Государственных свободных художественных мастерских (ГСХМ), преобразованных в дальнейшем в Высший художественно-технический институт (Вхутеин, 1920–1930). \r\n', '@uploads/author/drevin_aleksandr.jpg', 1),
(28, 'Жилинский Дмитрий Дмитриевич', '1927 - 2015\r\n\r\nЖивописец, график, монументалист. Портретист, пейзажист, жанрист. В 1944–1946 годах учился в Московском институте прикладного и декоративного искусства, в 1946–1951 – в Московском государственном художественном институте имени В.И.Сурикова у П.Д.Корина. С 1952 по 1974 год преподавал в Московском государственном художественном институте имени В.И.Сурикова.\r\n', '@uploads/author/jilinsky_dmitry.jpg', 1),
(29, 'Кабаков Илья Иосифович', 'род. в 1933, Новосибирск\r\n\r\nЖивописец, график, автор объектов и инсталляций. В 1943 поступил в Ленинградскую художественную школу Академии художеств, временно обосновавшуюся в Самарканде, куда ребенком был эвакуирован с началом Великой Отечественной войны. В 1951–1957 учился на графическом факультете Московского государственного художественного института имени В.И.Сурикова.\r\n', '@uploads/author/kabakov_ilya.jpg', 1),
(30, 'Кантор Максим Карлович', 'Род. в 1957\r\n\r\nЖивописец, график. В 1975–1980 учился в Московском полиграфическом институте. Работает в Лондоне и Москве. Среди молодых живописцев 1980-х годов творчество М.К.Кантора выделяется интересом к социальной теме, которая решается им с точки зрения общечеловеческих проблем.\r\n', '@uploads/author/kantor_maksim.jpg', 1),
(31, 'Коненков Сергей Тимофеевич', '1874, деревня Верхние Караковичи Смоленской губернии – 1971, Москва\r\n\r\nУчился в Московском училище живописи, ваяния и зодчества в 1892–1896 у С.И.Иванова и С.М.Волнухина; затем – в Высшем художественном училище при Императорской Академии художеств в Санкт-Петербурге в 1898–1902 у В.А.Беклемишева.\r\n', '@uploads/author/konenkov_sergey.jpg', 1),
(32, 'Кончаловский Петр Петрович', '1876, Славянск – 1956, Москва\r\n\r\nУчился в рисовальной школе М.Д.Раевской-Ивановой в Харькове. Посещал вечерние классы Строгановского училища в Москве в 1888. Обучался в Академии Р.Жюлиана в Париже в 1897–1898, затем – в Императорской Академии художеств в Санкт-Петербурге в 1898–1907.\r\n', '@uploads/author/konchalovsky_petr.jpg', 1),
(33, 'Коржев-Чувелев Гелий Михайлович', '1925, Москва - 2012, Москва\r\n\r\nУчился в Московской средней художественной школе (МСХШ, МГХШ) (1939 -1944) у В.В.Почиталова, М.В.Добросердова, А.О.Барща; в МГХИ (1944-1950) у С.В.Герасимова, В.В.Почиталова. Преподавал в МВХПУ (1951-1958, 1964-1973). Профессор (1966). Жил в Москве. Заслуженный деятель искусств. Действительный член Академии художеств СССР (1970). Народный художник СССР (1979).\r\n', '@uploads/author/korzhev-chuvelev_geliy.jpg', 1),
(34, 'Крымов Николай Петрович', '1884, Москва – 1958, Москва\r\n\r\nПервые уроки рисунка получил у отца художника-портретиста П.А.Крымова. Учился в Московском училище живописи, ваяния и зодчества в 1904–1911, вначале на архитектурном отделении, с 1907 – в живописном классе у Н.А.Касаткина, Л.О.Пастернака и А.М.Васнецова; занимался в мастерской В.А.Серова и К.А.Коровина. В 1905 сблизился с художниками символистического направления, участник выставки "Голубая Роза" (1907).\r\n', '@uploads/author/krymov_nikolai.jpg', 1),
(35, 'Кузнецов Павел Варфоломеевич', '1878, Саратов – 1968, Москва\r\n\r\nУчился в Рисовальном училище при Обществе любителей изящных искусств в Саратове (в 1891–1896, затем – в Московском училище живописи, ваяния и зодчества в 1897–1903 и в мастерской В.А.Серова и К.А.Коровина. Последователь творчества В.Э.Борисова-Мусатова.\r\n', '@uploads/author/kuznetsov_pavel.jpg', 1),
